  Data({
      List<Videos> videos}){
    _videos = videos;
}

  Data.fromJson(dynamic json) {
    if (json['videos'] != null) {
      _videos = [];
      json['videos'].forEach((v) {
        _videos.add(Videos.fromJson(v));
      });
    }
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    if (_videos != null) {
      map['videos'] = _videos.map((v) => v.toJson()).toList();
    }
    return map;
  }

}

